import React, { useState } from "react";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';




function TempContrl() {
  const [count, setCount] = useState(22);
  const [mode, setMode] = useState("Heat");
  const [temp, setTemp] = useState("C");

  function convertTemp() {
    if (temp === "C") {
      setCount((count * 9) / 5 + 32);
      setTemp("F");
    }

    if (temp === "F") {
      setCount(((count - 32) * 5) / 9);
      setTemp("C");
    }
  }
  return (
    <Container maxWidth="sm">
      <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
        <h2>Temperature Control</h2>
        </Grid>

        <Grid item xs={12}>
        <h1>{mode}</h1>
        </Grid>

        <Grid item xs={12}>
        <h1>
        {count} &nbsp; &#176;{temp}
        </h1>
        </Grid>

        <Grid item xs={12}>
        <Stack spacing={2} direction="row">
       <Button variant="contained" onClick={() => setCount(count - 1)}>-</Button>
        <Button variant="contained" onClick={() => setCount(count + 1)}>+</Button>
        </Stack>
        </Grid>       
       
        <Grid item xs={12}>
          <Button varinat="outlined" onClick={() => {setMode("Cold");}}>Cold</Button>
          <Button varinat="outlined" onClick={() => {setMode("Hot");}}>Hot</Button>
          <Button varinat="outlined" onClick={() => {setMode("Auto Mode");}}>Auto Mode</Button>
        </Grid>
         
         
        <Grid item xs={12}>
          <Button varinat="outlined" onClick={() => {setTemp("C");}}>Celsius</Button>
          <Button varinat="outlined" onClick={() => {setTemp("F");}}>Farhnet</Button>
        </Grid>
  
        <Grid item xs={12}>
          <Button varinat="outlined" onClick={() => {convertTemp("");}}>Convert Temperature</Button>
        </Grid>
      </Grid>
    </Box>

      <div>
 



 




      </div>
    </Container>
  );
}

export default TempContrl;
