import React, { useState } from "react";

function TempContrl() {
  const [count, setCount] = useState(22)
  const [mode, setMode] = useState("Heat")
  const [temp, setTemp] = useState("C")


  function convertTemp(){

    if (temp === "C"){
      setTemp(Temp+10)
    }


  }
  return (
    <div>
      <h2>Temperature Control</h2>
      <h1>{mode}</h1>
      <h1>{count} &nbsp; &#176;{temp}</h1>


      
      <button onClick={() => setCount(count - 1)}>-</button>
      &nbsp;
      <button onClick={() => setCount(count + 1)}>+</button>
      <br/><br/>

      <button onClick={()=>{setMode("Cold")}}>Cold</button>
      <button onClick={()=>{setMode("Hot")}}>Hot</button>
      <button onClick={()=>{setMode("Auto Mode")}}>Auto Mode</button>

      <br/>
      <br/>
      <br/>
      <button onClick={()=>{setTemp("Celsius")}}>Celsius</button>
      <button onClick={()=>{setTemp("Farhnet")}}>Farhnet</button>

      <button onClick={()=>{convertTemp()}}>Convert</button>



    </div>

  );
}

export default TempContrl;
