import React, { useState } from "react";

function TempContrl() {
  const [count, setCount] = useState(22)
  const [mode, setMode] = useState("Heat")
  const [celsius, setCelsius] = useState("F")
  return (
    <div>
      <h2>Temperature Control</h2>
      <h1>{mode}</h1>
      <h1>{count} {celsius}</h1>


      
      <button onClick={() => setCount(count - 1)}>-</button>
      &nbsp;
      <button onClick={() => setCount(count + 1)}>+</button>
      <br/><br/>

      <button onClick={()=>{setMode("Cold")}}>Cold</button>
      <button onClick={()=>{setMode("Hot")}}>Hot</button>
      <button onClick={()=>{setMode("Auto Mode")}}>Auto Mode</button>

      <br/>
      <br/>
      <br/>
      <button onClick={()=>{setCelsius("Celsius")}}>Celsius</button>
      <button onClick={()=>{setCelsius("Farhnet")}}>Farhnet</button>


    </div>

  );
}

export default TempContrl;
